SELECT
	count(*)
FROM
	tb_meeting m 
WHERE (m.meeting_status = 1 
	OR m.meeting_status = 2) 
	AND m.is_delete = 0
	AND m.is_secrecy = 0
	AND m.disable = 0

	AND m.start_time>= #{beginDate}


	AND m.start_time< #{endDate}


	AND m.city = #{city}


	AND m.city IS NOT NULL


	AND m.industry LIKE #{industry}


	AND m.meeting_name LIKE #{keyword}


	AND m.city <> #{city}

SELECT DISTINCT m.id AS id, m.start_time AS startTime, m.meeting_name AS meetingName, m.meeting_type AS meetingType, m.meeting_desc AS meetingDesc, m.meeting_status AS meetingStatus, m.meeting_address AS meetingAddress, m.meeting_address_pos_x AS meetingAddressPosX, m.meeting_address_pos_y AS meetingAddressPosY, m.create_id AS createId, m.create_name AS createName, m.end_time AS endTime, m.member_count AS memberCount, m.industry, m.top, m.disable, m.is_pay AS isPay, m.pay_money AS payMoney,
lat_long_distance(#{addressPosX},#{addressPosY},m.meeting_address_pos_x,m.meeting_address_pos_y) AS distance,
p.pic_path AS path FROM tb_meeting m LEFT JOIN tb_meeting_pic p ON m.id = p.meeting_id AND p.module_type = 1 AND p.ishome_page = 1 WHERE (m.meeting_status = 1 OR m.meeting_status = 2) AND m.is_delete = 0 AND m.is_secrecy = 0 AND m.disable = 0
AND m.start_time>= #{beginDate}
AND m.start_time< #{endDate}
AND m.city = #{city}
AND m.city<> #{city}
AND m.city is not null
AND m.industry LIKE #{industry}
AND m.meeting_name LIKE #{keyword}
ORDER BY
lat_long_distance(#{addressPosX},#{addressPosY},m.meeting_address_pos_x,m.meeting_address_pos_y),
m.meeting_status DESC, m.start_time DESC
LIMIT #{startRow},#{size}

SELECT DISTINCT id AS id, meeting_name AS meetingName, meeting_status AS meetingStatus, create_id AS createId, create_name AS createName, start_time as startTime, end_time AS endTime, province AS province, city, town, top, disable, is_pay as isPay FROM tb_meeting WHERE is_delete = 0
AND meeting_name LIKE concat('%',concat(#{keyword},'%'))
AND create_name LIKE concat('%',concat(#{keyword},'%'))
AND province = #{province}
AND city = #{city}
AND town = #{town}
AND start_time >= #{startTime}
AND end_time < #{endTime}
AND disable = #{disable}
ORDER BY start_time DESC
LIMIT #{startRow}, #{size}

