SELECT DISTINCT M.* FROM ( ( SELECT DISTINCT m.id AS id, m.start_time AS startTime, m.meeting_name AS meetingName, m.meeting_desc AS meetingDesc, m.meeting_type AS meetingType, m.meeting_status AS meetingStatus, m.create_id AS createId, m.create_name AS createName, m.meeting_address AS meetingAddress, m.meeting_address_pos_x AS meetingAddressPosX, m.meeting_address_pos_y AS meetingAddressPosY, m.top, m.disable, m.is_pay AS isPay, m.pay_money AS payMoney, p.pic_path AS path, CASE m.meeting_status WHEN 3 THEN -1 ELSE m.meeting_status END AS orderType FROM tb_meeting m LEFT JOIN tb_meeting_pic AS p ON p.meeting_id = m.id AND p.module_type = 1 AND p.ishome_page = 1 WHERE m.meeting_status = 0 AND m.is_delete = 0 AND m.create_id = #{memberId} ) UNION ALL ( SELECT DISTINCT m.id AS id, m.start_time AS startTime, m.meeting_name AS meetingName, m.meeting_desc AS meetingDesc, m.meeting_type AS meetingType, m.meeting_status AS meetingStatus, m.create_id AS createId, m.create_name AS createName, m.meeting_address AS meetingAddress, m.meeting_address_pos_x AS meetingAddressPosX, m.meeting_address_pos_y AS meetingAddressPosY, m.top, m.disable, m.is_pay AS isPay, m.pay_money AS payMoney, p.pic_path AS path, CASE m.meeting_status WHEN 3 THEN -1 ELSE m.meeting_status END AS orderType FROM tb_meeting m LEFT JOIN tb_meeting_pic AS p ON p.meeting_id = m.id AND p.module_type = 1 AND p.ishome_page = 1 INNER JOIN tb_meeting_member mm ON m.id = mm.meeting_id WHERE m.meeting_status = 2 AND m.is_delete = 0 AND mm.is_delete = 0 AND mm.member_id = #{memberId} AND ( ( m.create_id <> #{memberId} AND mm.is_delete = 0 AND ( (mm.attend_meet_type = 1 AND mm.attend_meet_status = 4 AND mm.excute_meet_sign = 1) OR (mm.attend_meet_type = 0 AND mm.attend_meet_status = 1) ) ) OR (m.create_id = #{memberId} AND mm.is_delete = 0) ) ) UNION ALL ( SELECT DISTINCT m.id AS id, m.start_time AS startTime, m.meeting_name AS meetingName, m.meeting_desc AS meetingDesc, m.meeting_type AS meetingType, m.meeting_status AS meetingStatus, m.create_id AS createId, m.create_name AS createName, m.meeting_address AS meetingAddress, m.meeting_address_pos_x AS meetingAddressPosX, m.meeting_address_pos_y AS meetingAddressPosY, m.top, m.disable, m.is_pay AS isPay, m.pay_money AS payMoney, p.pic_path AS path, CASE m.meeting_status WHEN 3 THEN -1 ELSE m.meeting_status END AS orderType FROM tb_meeting m LEFT JOIN tb_meeting_pic AS p ON p.meeting_id = m.id AND p.module_type = 1 AND p.ishome_page = 1 INNER JOIN tb_meeting_member mm ON m.id = mm.meeting_id WHERE (m.meeting_status = 1 OR m.meeting_status = 3) AND m.is_delete = 0 AND mm.is_delete = 0 AND mm.member_id = #{memberId} AND ( ( m.create_id <> #{memberId} AND ( (mm.attend_meet_type = 1 AND mm.attend_meet_status = 4 AND mm.excute_meet_sign = 1) OR (mm.attend_meet_type = 0 AND mm.attend_meet_status = 1) ) ) OR (m.create_id = #{memberId}) ) ) ) M
WHERE M.id IS NOT NULL
AND M.meetingName LIKE #{keyword}
AND M.meetingName LIKE #{keyword}
AND EXISTS (SELECT N.id FROM tb_meeting_people N WHERE N.meeting_id = M.id
AND N.people_name LIKE #{keyword}
)
AND EXISTS (SELECT N.id FROM tb_meeting_organ N WHERE N.meeting_id = M.id
AND N.organ_name LIKE #{keyword}
)
AND EXISTS (SELECT N.id FROM tb_meeting_data N WHERE N.meeting_id = M.id AND data_type = 1
AND N.data_name LIKE #{keyword}
)
AND EXISTS (SELECT N.id FROM tb_meeting_data N WHERE N.meeting_id = M.id AND data_type = 0
AND N.data_name LIKE #{keyword}
)
AND EXISTS (SELECT N.id FROM tb_meeting_note N WHERE N.meeting_id = M.id AND N.creater = #{memberId}
AND N.meeting_note_title LIKE #{keyword}
)
AND M.createId = #{memberId}
AND M.createId <> #{memberId}
ORDER BY M.orderType DESC, M.startTime DESC
LIMIT #{startRow}, #{size}

SELECT count(*) FROM (
	(
		SELECT DISTINCT m.id,
			m.meeting_name AS meetingName,
			m.create_id AS createId
		FROM tb_meeting m 
		LEFT JOIN tb_meeting_pic AS p 
			ON p.meeting_id = m.id
			AND p.module_type = 1 
			AND p.ishome_page = 1 
		WHERE m.meeting_status = 0 
			AND m.is_delete = 0
			AND m.create_id = #{memberId}
	) UNION ALL (
		SELECT DISTINCT m.id,
			m.meeting_name AS meetingName,
			m.create_id AS createId
		FROM tb_meeting m
		LEFT JOIN tb_meeting_pic AS p 
			ON p.meeting_id = m.id
			AND p.module_type = 1 
			AND p.ishome_page = 1 
		INNER JOIN tb_meeting_member mm 
			ON m.id = mm.meeting_id 
		WHERE m.meeting_status IN (1,2,3)
			AND m.is_delete = 0 
			AND mm.is_delete = 0
			AND mm.member_id = #{memberId} 
			AND (
				(
					m.create_id  <> #{memberId} 
					AND (
						(mm.attend_meet_type = 1 AND mm.attend_meet_status = 4 AND mm.excute_meet_sign = 1)
						OR (mm.attend_meet_type = 0 AND mm.attend_meet_status = 1)
					) 
				)
				OR (m.create_id = #{memberId})
			)
	)
) M
WHERE M.id IS NOT NULL



    		AND M.meetingName LIKE #{keyword}




    		AND M.meetingName LIKE #{keyword}



		AND EXISTS (SELECT N.id FROM tb_meeting_people N WHERE N.meeting_id = M.id

			AND N.people_name LIKE #{keyword}

		)


		AND EXISTS (SELECT N.id FROM tb_meeting_organ N WHERE N.meeting_id = M.id

			AND N.organ_name LIKE #{keyword}

		)


		AND EXISTS (SELECT N.id FROM tb_meeting_data N WHERE N.meeting_id = M.id AND data_type = 1

			AND N.data_name LIKE #{keyword}

		)


		AND EXISTS (SELECT N.id FROM tb_meeting_data N WHERE N.meeting_id=M.id AND data_type=0

			AND N.data_name LIKE #{keyword}

		)


		AND EXISTS (SELECT N.id FROM tb_meeting_note N WHERE N.meeting_id=M.id AND N.creater=#{memberId}

			AND N.meeting_note_title LIKE #{keyword}

		)




		AND M.createId = #{memberId}


		AND M.createId <> #{memberId}

