mapper.select:
		select id as id,
		muc_id as mucId,
		topic_id as topicId,
		type as type,
		user_id as userId,
		created_time as createdTime
		from t_im_social_status
		where 1=1

                        and id = #{id} 


                        and muc_id = #{mucId} 


                        and type = #{type} 


                        and user_id = #{userId} 


                        and created_time = #{createdTime}

mapper.delete:
		DELETE FROM t_im_social_status WHERE

                        and id = #{id} 


                        and muc_id = #{mucId} 


                        and type = #{type} 


                        and user_id = #{userId} 


                        and created_time = #{createdTime}
